<?php

namespace App\Service;

use Dompdf\Dompdf;

class PDFService {

    public const logoBase64 = " VWAw2wX/C34Ofgi+DW4H19M5KBQT8n9myk5kHrCrZgAAAABJRU5ErkJggg==";

    public function create(string $filename, string $html): void {

        $pdf = new Dompdf();

        $pdf->loadHtml($html);
        $pdf->render();
        $pdf->stream($filename, [
            'compress' => true,
            "Attachment" => false
        ]);

    }

}
